import React ,{useEffect,useState}from 'react';
import Form from '../Components/Form';
import Posts from '../Components/Posts';
import axios from 'axios';

export default function Home() {
  const [currentId, setCurrentId] = useState(0);
  const [posts,setPost]=useState([]);
    
  const getAllPosts = async()=>{
  const response=await axios.get("http://localhost:5000/posts/getall");
  const data=response.data;
  setPost(data);
  console.log(posts)
  }

  useEffect (()=>{
      getAllPosts();
      
  },[]);
  const post = currentId ? posts.find((message) => message._id === currentId) : null;
  
  return (
    <>
     <Form post={post} currentId={currentId} setCurrentId={setCurrentId} />
    <Posts posts={posts} setPost={setPost}  currentId={currentId} setCurrentId={setCurrentId}/>
    </>
   
  )
}
