import { React} from "react";
import moment from "moment";
import Card from "@mui/material/Card";
import CardHeader from "@mui/material/CardHeader";
import CardMedia from "@mui/material/CardMedia";
import CardContent from "@mui/material/CardContent";
import CardActions from "@mui/material/CardActions";
import Avatar from "@mui/material/Avatar";
import IconButton from "@mui/material/IconButton";
import Typography from "@mui/material/Typography";
import { red } from "@mui/material/colors";
import DeleteIcon from '@mui/icons-material/Delete';
import ThumbUpAltIcon from '@mui/icons-material/ThumbUpAlt';
import MoreVertIcon from "@mui/icons-material/MoreVert";
import CssBaseline from "@mui/material/CssBaseline";
import Container from "@mui/material/Container";
import Grid from "@mui/material/Grid";
import { likePost,deletePost } from "../api";
import { createTheme, ThemeProvider } from "@mui/material/styles";



const theme = createTheme();

export default function Posts({ posts, setCurrentId }) {
  const user = JSON.parse(localStorage.getItem('profile'));
  const [likes, setLikes] = useState(post?.likes);
  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <main>
        <Container sx={{ py: 8 }} maxWidth="md">
          {/* End hero unit */}
          <Grid container spacing={4}>
            {posts.map((post) => (
              <Grid item key={post._id} xs={12} sm={6} md={4}>
                <Card sx={{ maxWidth: 345 }}>
                  <CardHeader
                    avatar={
                      <Avatar sx={{ bgcolor: red[500] }} aria-label="recipe">
                        R
                      </Avatar>
                    }
                    action={
                      <IconButton onClick={() => setCurrentId(post._id)} aria-label="settings">
                        <MoreVertIcon />
                      </IconButton>
                    }
                    title={post.title}
                    subheader={moment(post.createdAt).fromNow()}
                  />
                  <CardMedia
                    component="img"
                    
                    height="194"
                    image={post.selectedFile}
                    alt="postImage"
                  />
                  <CardContent>
                    <Typography variant="body2" color="text.secondary">
                      {post.message}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      {post.tags.map((tag) => `#${tag} `)}
                    </Typography>
                  </CardContent>
                {user && <CardActions disableSpacing>
                    <IconButton aria-label="add to favorites" onClick={() => likePost(post._id)}>
                    <ThumbUpAltIcon fontSize="small"  /> 
                    {post.likes.length}
                    </IconButton>
                    <IconButton aria-label="delete" onClick={() => deletePost(post._id)}>
                      <DeleteIcon />
                    </IconButton>
                 
                  </CardActions>
                  }  
                </Card>
              </Grid>
            ))}
          </Grid>
        </Container>
      </main>
    </ThemeProvider>
  );
}
