import React, { useState } from 'react';
import { TextField, Button, Typography, Paper, Container } from '@mui/material';
import FileBase from 'react-file-base64';
import axios from 'axios'




export default function Form() {
   
    const [postData, setPostData] = useState({ creator: '', title: '', message: '', tags: '', selectedFile: '' });
   
    
      const clear = () => {
        setPostData({ creator: '', title: '', message: '', tags: '', selectedFile: '' });
      };
    
      const handleSubmit = async (e) => {
        e.preventDefault();       
        const Data = {
      creator: postData.creator,
      title:postData.title,
      message:postData.message,
      tags: postData.tags,
      selectedFile:postData.selectedFile
    };
    axios.post("http://localhost:5000/posts/createpost", Data).then((response) => {
      console.log(response.status, response.data);
    }); 
        }
      
  
  
    return (
      <Container>
 <Paper>
    <form autoComplete="off" noValidate  onSubmit={handleSubmit}>
      <Typography variant="h6">creating a Post</Typography>
      <TextField name="creator" variant="outlined" label="Creator" fullWidth value={postData.creator} onChange={(e) => setPostData({ ...postData, creator: e.target.value })} />
      <TextField name="title" variant="outlined" label="Title" fullWidth value={postData.title} onChange={(e) => setPostData({ ...postData, title: e.target.value })} />
      <TextField name="message" variant="outlined" label="Message" fullWidth multiline rows={4} value={postData.message} onChange={(e) => setPostData({ ...postData, message: e.target.value })} />
      <TextField name="tags" variant="outlined" label="Tags (coma separated)" fullWidth value={postData.tags} onChange={(e) => setPostData({ ...postData, tags: e.target.value.split(',') })} />
      <div sx={{m}}><FileBase type="file" multiple={false} onDone={({ base64 }) => setPostData({ ...postData, selectedFile: base64 })} /></div>
      <Container sx={{ display: { lg: "flex", md: "block" }, mx:"auto", my:"1rem" }}><Button variant="contained" color="primary" size="large" type="submit" fullWidth>Submit</Button>
      <Button sx={{my:"1rem"}} variant="contained" color="secondary" size="large" onClick={clear} fullWidth>Clear</Button></Container>
    </form>
  </Paper>

      </Container>
   
  )
}
