import axios from "axios";
const baseURL = "http://localhost:5000";
axiosInstance.interceptors.request.use(
  (config) => {
    const user = JSON.parse(localStorage.getItem("user"));

    if (user) {
      config.headers.Authorization = `Bearer ${user.token}`;
    }
    console.log("request config", config);
    return config;
  },
  (error) => {
    // console.log("request error", error);
    return Promise.reject(error);
  }
);

export const createPost = (dataURL) => {
  axios
    .post("http://localhost:5000/posts/createpost", dataURL)
    .then((response) => {
      console.log(response.status, response.data);
    });
};

export const updatePost = (dataURL, id) => {
  axios
    .patch(`http://localhost:5000/posts/updatepost/${id}`, dataURL)
    .then((response) => {
      console.log(response.status, response.data);
    });
};

export const likePost = (id) => {
  axios.patch(`http://localhost:5000/posts/${id}/likepost`).then((response) => {
    console.log(response.status, response.data);
  });
};

export const deletePost = (id) => {
  axios.delete(`http://localhost:5000/posts/${id}`).then((response) => {
    console.log(response.status, response.data);
  });
};

export const signIn =async (formData) => await axiosInstance.post('/user/signin', formData);
export const signUp =async (formData) => await axiosInstance.post('/user/signup', formData);
