import React, { useEffect, useState } from 'react';
import { TextField, Button, Typography, Paper, Container } from '@mui/material';
import FileBase from 'react-file-base64';
import axios from 'axios'




export default function Form({currentId,setCurrentId,post,setPost}) {
   
    const [postData, setPostData] = useState({ creator: '', title: '', message: '', tags: '', selectedFile: '' });
   
    
      const clear = () => {
        setCurrentId(0);
        setPostData({ creator: '', title: '', message: '', tags: '', selectedFile: '' });
      };

      useEffect (()=>{
        if (!post?.title) clear();
    if (post) setPostData(post);
      },[post])
    
      const handleSubmit = async (e) => {
        e.preventDefault();       
        const Data = {
      creator: postData.creator,
      title:postData.title,
      message:postData.message,
      tags: postData.tags,
      selectedFile:postData.selectedFile
    };
    if(currentId === 0){
      axios.post("http://localhost:5000/posts/createpost", Data).then((response) => {
      console.log(response.status, response.data);
    });
    clear();
    }else{
      axios.patch("http://localhost:5000/posts/updatepost/:"+currentId,Data).then((response) => {
        console.log(response.status, response.data);
      });
      clear();
    }
     
        }
      
  
  
    return (
      <Container>
 <Paper>
    <form autoComplete="off" noValidate  onSubmit={handleSubmit}>
    <Typography variant="h6">{currentId ? `Editing "${post?.title}"` : 'Creating a Memory'}</Typography>
      <Typography sx={{my:"2rem"}} variant="h6">creating a Post</Typography>
      <TextField sx={{my:"1rem"}} name="creator" variant="outlined" label="Creator" fullWidth value={post.creator} onChange={(e) => setPost({ ...postData, creator: e.target.value })} />
      <TextField sx={{my:"1rem"}} name="title" variant="outlined" label="Title" fullWidth value={post.title} onChange={(e) => setPost({ ...postData, title: e.target.value })} />
      <TextField sx={{my:"1rem"}} name="message" variant="outlined" label="Message" fullWidth multiline rows={4} value={postData.message} onChange={(e) => setPostData({ ...postData, message: e.target.value })} />
      <TextField sx={{my:"1rem"}} name="tags" variant="outlined" label="Tags (coma separated)" fullWidth value={postData.tags} onChange={(e) => setPostData({ ...postData, tags: e.target.value.split(',') })} />
      <div><FileBase type="file" multiple={false} onDone={({ base64 }) => setPostData({ ...postData, selectedFile: base64 })} /></div>
      <Container sx={{ mx:"auto", my:"1rem" }}><Button variant="contained" color="primary" size="large" type="submit" fullWidth>Submit</Button>
      <Button sx={{my:"1rem"}} variant="contained" color="secondary" size="large" onClick={clear} fullWidth>Clear</Button></Container>
    </form>
  </Paper>

      </Container>
   
  )
}
