import React,{useState} from "react";
import "./App.css";
import { BrowserRouter, Routes, Route} from "react-router-dom";
import Header from "./Components/Header";
import Footer from "./Components/Footer";
import Home from "./Pages/Home";
import Authentification from "./Pages/Authentification";
import { AuthContext } from "./auth";

function App() {
  const [authTokens, setAuthTokens] = useState(
    localStorage.getItem("tokens") || ""
  );
  const setTokens = (data) => {
    localStorage.setItem("tokens", JSON.stringify(data));
    setAuthTokens(data);
  };

  console.log("authTokens", authTokens.oldUser._id);


  return (
    <AuthContext.Provider value={{ authTokens, setAuthTokens: setTokens }}>
<div className="App">
      <BrowserRouter>
        <Header setAuthTokens={setAuthTokens} authTokens={authTokens}/>
        <Routes>
          <Route path="/" element={<Home setAuthTokens={setAuthTokens} authTokens={authTokens} />} exact />
          <Route path="/auth" element={<Authentification setAuthTokens={setAuthTokens} authTokens={authTokens} />} />
        </Routes>
        <Footer />
      </BrowserRouter>
    </div>
    </AuthContext.Provider>

  );
}

export default App;
