import axios from 'axios';
const API = axios.create({ baseURL: 'http://localhost:5000' });

API.interceptors.request.use((req) => {
  if (localStorage.getItem('profile')) {
    req.headers.Authorization = `Bearer ${JSON.parse(localStorage.getItem('profile')).token}`;
  }

  return req;
});

export const createPost =(dataURL)=>{
    axios.post("http://localhost:5000/posts/createpost", dataURL).then((response) => {
      console.log(response.status, response.data);
})
}

export const updatePost =(dataURL,id)=>{
    axios.patch(`http://localhost:5000/posts/updatepost/${id}`, dataURL).then((response) => {
        console.log(response.status, response.data); 
})
}

export const likePost =(id)=>{
    axios.patch(`http://localhost:5000/posts/${id}/likepost`).then((response) => {
      console.log(response.status, response.data);
})
}

export const deletePost =(id)=>{
    axios.delete(`http://localhost:5000/posts/${id}`).then((response) => {
      console.log(response.status, response.data);
})
}

export const signIn = (formData) => API.post('/user/signin', formData);
export const signUp = (formData) => API.post('/user/signup', formData);
